css:
.github-HunkView {
  font-family: Menlo, Consolas, 'DejaVu Sans Mono', monospace;
  border-bottom: 1px solid #1b1c22;
  background-color: #2d2f39;
}
.github-HunkView-header {
  display: flex;
  align-items: stretch;
  font-size: .9em;
  background-color: #2d2f39;
  border-bottom: 1px solid #1b1c22;
}
.github-HunkView-title {
  flex: 1;
  line-height: 2.4;
  padding: 0 10px;
  color: rgba(158, 161, 179, 0.6);
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  -webkit-font-smoothing: antialiased;
}
.github-HunkView-stageButton,
.github-HunkView-discardButton {
  line-height: 1;
  padding-left: 10px;
  padding-right: 10px;
  font-family: 'BlinkMacSystemFont', 'Lucida Grande', 'Segoe UI', Ubuntu, Cantarell, sans-serif;
  border: none;
  border-left: 1px solid #1b1c22;
  background-color: transparent;
  cursor: default;
}
.github-HunkView-stageButton:hover,
.github-HunkView-discardButton:hover {
  background-color: #3f4250;
}
.github-HunkView-stageButton:active,
.github-HunkView-discardButton:active {
  background-color: #1b1c22;
}
.github-HunkView-discardButton:before {
  text-align: left;
  width: auto;
}
.github-HunkView-line {
  display: table-row;
  line-height: 1.5em;
  color: rgba(158, 161, 179, 0.6);
}
.github-HunkView-line.is-unchanged {
  -webkit-font-smoothing: antialiased;
}
.github-HunkView-lineNumber {
  display: table-cell;
  min-width: 3.5em;
  overflow: hidden;
  padding: 0 .5em;
  text-align: right;
  border-right: 1px solid #1b1c22;
  -webkit-font-smoothing: antialiased;
}
.github-HunkView-plusMinus {
  margin-right: 1ch;
  color: rgba(158, 161, 179, 0.5);
}
.github-HunkView-lineContent {
  display: table-cell;
  padding: 0 .5em 0 3ch;
  text-indent: -2ch;
  white-space: pre-wrap;
  word-break: break-word;
  width: 100%;
}
.github-HunkView.is-selected.is-hunkMode .github-HunkView-header {
  background-color: #3f4250;
}
.github-HunkView.is-selected.is-hunkMode .github-HunkView-header .github-HunkView-title {
  color: #9ea1b3;
}
.github-HunkView.is-selected.is-hunkMode .github-HunkView-header .github-HunkView-stageButton,
.github-HunkView.is-selected.is-hunkMode .github-HunkView-header .github-HunkView-discardButton {
  border-color: #575a69;
}
.github-HunkView-title:hover {
  color: #d7d8e0;
}
.github-HunkView-line.is-deleted:hover {
  background-color: #363845;
}
.github-HunkView-line.is-deleted.is-selected {
  color: #9ea1b3;
  background-color: #3f4250;
}
.github-HunkView-line.is-deleted .github-HunkView-lineContent {
  color: #e8b8b8;
  background-color: #512533;
}
.github-FilePatchView:focus .github-HunkView-line.is-deleted.is-selected .github-HunkView-lineContent {
  color: #dfc8ce;
  background-color: #5d2b31;
}
.github-HunkView-line.is-added:hover {
  background-color: #363845;
}
.github-HunkView-line.is-added.is-selected {
  color: #9ea1b3;
  background-color: #3f4250;
}
.github-HunkView-line.is-added .github-HunkView-lineContent {
  color: #b9dfd3;
  background-color: #224b3f;
}
.github-FilePatchView:focus .github-HunkView-line.is-added.is-selected .github-HunkView-lineContent {
  color: #c9dbdc;
  background-color: #26513b;
}
.github-HunkView-line.is-deleted + .is-added .github-HunkView-lineContent {
  box-shadow: 0 -1px 0 rgba(128, 128, 128, 0.1);
}
.github-FilePatchView:focus .github-HunkView.is-selected.is-hunkMode .github-HunkView-title,
.github-FilePatchView:focus .github-HunkView.is-selected.is-hunkMode .github-HunkView-header,
.github-FilePatchView:focus .github-HunkView-line.is-selected .github-HunkView-lineNumber {
  color: #ffffff;
  background: #4f65d1;
}
.github-FilePatchView:focus .github-HunkView-line.is-selected .github-HunkView-lineNumber {
  border-color: #4253a5;
}
.github-FilePatchView:focus .github-HunkView.is-selected.is-hunkMode .github-HunkView-stageButton,
.github-FilePatchView:focus .github-HunkView.is-selected.is-hunkMode .github-HunkView-discardButton {
  border-color: #4555a3;
}
.github-FilePatchView:focus .github-HunkView.is-selected.is-hunkMode .github-HunkView-stageButton:hover,
.github-FilePatchView:focus .github-HunkView.is-selected.is-hunkMode .github-HunkView-discardButton:hover {
  background-color: #4c5fc1;
}
.github-FilePatchView:focus .github-HunkView.is-selected.is-hunkMode .github-HunkView-stageButton:active,
.github-FilePatchView:focus .github-HunkView.is-selected.is-hunkMode .github-HunkView-discardButton:active {
  background-color: #4f65d1;
}
